import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './Research.css';

const Research: React.FC = () => {
    const [selectedPdf, setSelectedPdf] = useState<string | null>(null);
    const [documents, setDocuments] = useState([
        {
            id: '1',
            title: 'Sistemas Ciberfísicos Inteligentes',
            path: '/docs/1.pdf',
            description: 'Investigación sobre integración de sistemas físicos y digitales'
        },
        {
            id: '2',
            title: 'IoT y Automatización',
            path: '/docs/2.pdf',
            description: 'Avances en Internet de las Cosas y automatización industrial'
        },
        {
            id: '3',
            title: 'Inteligencia Artificial',
            path: '/docs/3.pdf',
            description: 'Aplicaciones de IA en sistemas ciberfísicos'
        }
    ]);

    const onDragEnd = (result: any) => {
        if (!result.destination) return;

        const items = Array.from(documents);
        const [reorderedItem] = items.splice(result.source.index, 1);
        items.splice(result.destination.index, 0, reorderedItem);

        setDocuments(items);
    };

    return (
        <section className="research-section" id="research">
            <div className="research-container">
                <h2>Documentos de Investigación GISICF</h2>

                <DragDropContext onDragEnd={onDragEnd}>
                    <Droppable droppableId="documents">
                        {(provided) => (
                            <div 
                                className="documents-grid"
                                {...provided.droppableProps}
                                ref={provided.innerRef}
                            >
                                {documents.map((doc, index) => (
                                    <Draggable 
                                        key={doc.id} 
                                        draggableId={doc.id} 
                                        index={index}
                                    >
                                        {(provided) => (
                                            <div
                                                className="document-card"
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                onClick={() => setSelectedPdf(doc.path)}
                                            >
                                                <div className="document-icon">📄</div>
                                                <h3>{doc.title}</h3>
                                                <p>{doc.description}</p>
                                                <button className="view-btn">Ver PDF</button>
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>
                </DragDropContext>

                {selectedPdf && (
                    <div className="pdf-modal" onClick={() => setSelectedPdf(null)}>
                        <div className="pdf-viewer" onClick={e => e.stopPropagation()}>
                            <button 
                                className="close-btn"
                                onClick={() => setSelectedPdf(null)}
                            >
                                ×
                            </button>
                            <iframe 
                                src={selectedPdf}
                                title="PDF Viewer"
                                width="100%"
                                height="100%"
                            />
                        </div>
                    </div>
                )}
            </div>
        </section>
    );
};

export default Research;
