import React, { useState, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import './Research.css';

pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

const Research: React.FC = () => {
    const [selectedPdf, setSelectedPdf] = useState<string | null>(null);
    const [pdfUrls, setPdfUrls] = useState<{[key: string]: string}>({});

    const documents = [
        {
            id: '1',
            title: 'Sistemas Ciberfísicos Inteligentes',
            path: '/docs/1.pdf',
            description: 'Investigación sobre integración de sistemas físicos y digitales'
        },
        {
            id: '2',
            title: 'IoT y Automatización',
            path: '/docs/2.pdf',
            description: 'Avances en Internet de las Cosas y automatización industrial'
        },
        {
            id: '3',
            title: 'Inteligencia Artificial',
            path: '/docs/3.pdf',
            description: 'Aplicaciones de IA en sistemas ciberfísicos'
        }
    ];

    const loadPdfPreview = async (path: string) => {
        const loadingTask = pdfjsLib.getDocument(path);
        const pdf = await loadingTask.promise;
        const page = await pdf.getPage(1);
        const viewport = page.getViewport({ scale: 0.5 });

        const canvas = document.createElement('canvas');
        const context = canvas.getContext('2d');
        canvas.height = viewport.height;
        canvas.width = viewport.width;

        await page.render({
            canvasContext: context!,
            viewport: viewport
        }).promise;

        return canvas.toDataURL();
    };

    useEffect(() => {
        documents.forEach(async (doc) => {
            try {
                const url = await loadPdfPreview(doc.path);
                setPdfUrls(prev => ({ ...prev, [doc.id]: url }));
            } catch (error) {
                console.error('Error loading PDF:', error);
            }
        });
    }, []);

    return (
        <div className="research-section">
            <div className="research-container">
                <h2>Documentos de Investigación GISICF</h2>
                <div className="pdf-grid">
                    {documents.map((doc) => (
                        <div key={doc.id} className="pdf-card" onClick={() => setSelectedPdf(doc.path)}>
                            {pdfUrls[doc.id] && (
                                <div className="pdf-preview">
                                    <img src={pdfUrls[doc.id]} alt={doc.title} />
                                </div>
                            )}
                            <h3>{doc.title}</h3>
                            <p>{doc.description}</p>
                        </div>
                    ))}
                </div>

                {selectedPdf && (
                    <div className="pdf-modal" onClick={() => setSelectedPdf(null)}>
                        <div className="modal-content" onClick={e => e.stopPropagation()}>
                            <button className="close-button" onClick={() => setSelectedPdf(null)}>×</button>
                            <iframe src={selectedPdf} title="PDF Viewer" />
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Research;
