import React from 'react';
import './Hero.css'; // Assuming you will create a separate CSS file for Hero component styles

const Hero: React.FC = () => {
    return (
        <div className="hero" style={{ backgroundColor: '#add8e6', padding: '50px', textAlign: 'center' }}>
            <h1>Welcome to Our Research Group</h1>
            <p>Join us in exploring innovative solutions and advancing knowledge.</p>
            <button style={{ backgroundColor: '#90ee90', border: 'none', padding: '10px 20px', cursor: 'pointer' }}>
                Get Involved
            </button>
        </div>
    );
};

export default Hero;
